/**
 * Переводит число `1` в строку "01"
 */
export declare function num2str(num: string | number): string;
/**
 * Переводит объект `Date` в строку формата "dd.mm.yy"
 */
export declare function date2str(date: Date): string;
/**
 * Переводит строку формата "dd.mm.yy" в объект `Date`
 */
export declare function str2date(str: string): Date;
/**
 * Переводит строку формата "dd.mm.yy" в строку формата "yyyy-mm-ddT00:00"
 */
export declare function date2JSON(str: string): string;
/**
 * Переводит объект `Date` в строку формата "yyyy-mm-ddT00:00"
 */
export declare function date2JSON(date: Date): string;
